interface MenuItem {
  id: number;
  href: string;
  title: string;
}

// Desktop Navigation Component
const DesktopNav = ({
  onePage,
  menus,
}: {
  onePage?: boolean;
  menus?: MenuItem[];
}) => {
  const getRouteForMenu = (href: string) => {
    // Map menu items to actual routes
    const routeMap: { [key: string]: string } = {
      home: "/",
      about: "/#about", // Link to home page about section
      features: "/#features", // Link to home page features section
      courses: "/courses", // Always link to courses page
      blog: "/blog", // Link to blog page
      contact: "/contact", // Link to contact page
      "course-details": "/course-details"
    };
    
    return routeMap[href] || `/${href}`;
  };

  // Menu items that should always use route navigation (not anchor links)
  const alwaysRouteItems = ['courses', 'blog', 'contact'];

  return (
    <div className="navbar-collapse clearfix">
      {onePage ? (
        <ul className="navigation onepage clearfix">
          {menus?.map((menu) => (
            <li key={menu.id}>
              {alwaysRouteItems.includes(menu.href) ? (
                <Link href={getRouteForMenu(menu.href)}>
                  {menu.title}
                </Link>
              ) : (
                <a href={`#${menu.href}`}>{menu.title}</a>
              )}
            </li>
          ))}
        </ul>
      ) : (
        <ul className="navigation clearfix">
          {menus?.map((menu) => (
            <li key={menu.id}>
              <Link href={getRouteForMenu(menu.href)}>
                {menu.title}
              </Link>
            </li>
          ))}
        </ul>
      )}
    </div>
  );
};

